  import {formatAmount, formatDate, formatImg, getUserId, setTitle} from 'common/js/util';
  import defaltAvatarImg from './../../common/image/avatar@2x.png';
  import { getAdoptList } from 'api/biz';

  export default {
    data() {
      return {
        toastText: '',
        loading: false,
        loadingText: '',
        dataList: [],
        pullUpLoad: null
      };
    },
    mounted() {
      let history = this.$route.query.history || false;
      let type = this.$route.query.type || '';
      if(history) {
        setTitle('历史认养人');
        this.title = '历史认养人';
      } else {
        if(type && type === '3') {
          setTitle('已捐赠名单');
          this.title = '已捐赠名单';
        } else {
          setTitle('已认养名单');
          this.title = '已认养名单';
        }
      }
      this.getInitData();
    },
    methods: {
      getInitData() {
        this.getAdoptList();
      },
      getAdoptList() {
        this.loading = true;
        this.code = this.$route.query.code;
        Promise.all([
          getAdoptList({
            productCode: this.code,
            statusList: [1, 2, 3]
          })
        ]).then(([res1]) => {
          this.dataList = res1;
          this.loading = false;
        }).catch(() => {
          this.loading = false;
        });
      },
      go(url) {
        this.$router.push(url);
      },
      goUserHome(item) {
        if(getUserId()) {
          if(item.currentHolder === getUserId()) {
            // this.go(`/homepage`);
            this.go(`/my-tree?aTCode=${item.code}`);
          } else {
            // this.go(`/homepage?other=1&currentHolder=${item.currentHolder}`);
            this.go(`/my-tree?other=1&currentHolder=${item.currentHolder}&aTCode=${item.code}`);
          }
        } else {
          this.toastText = '您未登录';
          this.$refs.toast.show();
          setTimeout(() => {
            this.$router.push('/login');
          }, 1000);
        }
      },
      formatAmount(amount) {
        return formatAmount(amount);
      },
      formatDate(date, format) {
        return formatDate(date, format);
      },
      getImgSyl(imgs) {
        let img = imgs ? formatImg(imgs) : defaltAvatarImg;
        return {
          backgroundImage: `url(${img})`
        };
      },
      jiami(mobile) {
        return mobile.substr(0, 3) + '****' + mobile.substr(7);
      }
    },
    components: {
      Scroll,
      MHeader,
      NoResult,
      FullLoading,
      Toast,
      CategoryList
    }
  };
</script>
